mapper.select:
select

from tb_part_repertory pr
where pr.deleted = 0
and pr.storage_id = #{storageId,jdbcType=VARCHAR}
and pr.p_id = #{pId,jdbcType=VARCHAR}

select count(1) from tb_part_repertory pr left join tb_part tp on pr.p_id = tp.p_id left join sys_attr_value av on pr.storage_id = av.attr_id left join sys_attr_group ag on ag.attr_group_id = av.attr_group_id where pr.deleted = 0 and tp.deleted = 0 and av.deleted = 0 and ag.group_code = 'part.stock.storagelist'
and pr.pId = #{pId}
and tp.p_code like #{partCode}
and tp.p_name like #{partName}
and pr.storage_id = #{storageId}

select
, tp.p_code pCode,tp.p_name pName,av.attr_value storageName,u.real_name modifierName,DATE_FORMAT(pr.modified,'%Y-%m-%d') modifiedStr from tb_part_repertory pr left join tb_part tp on pr.p_id = tp.p_id left join sys_attr_value av on pr.storage_id =av.attr_id left join sys_user u on u.user_id = pr.modifier where pr.deleted = 0 and tp.deleted = 0 and av.deleted = 0
and pr.p_id = #{pId}
and tp.p_code like #{partCode}
and tp.p_name like #{partName}
and pr.storage_id = #{storageId}
group by pr.id order by pr.id limit #{start} , #{limit}
