mapper.select:
select count(1)
from tb_part_repertory_record rr
left join tb_part_repertory_record_out ro on rr.id = ro.repertory_record_id and ro.deleted = 0
left join tb_customer tc on tc.c_id = ro.customer_id
left join tb_contract con on con.id = ro.contract_id
left join sys_user su on su.user_id = ro.creator
where rr.deleted = 0
and rr.status = 1

    and (tc.c_code like #{customerKey} or tc.c_name like #{customerKey})


    and con.a_code = #{contractCode}


    and rr.code = #{outCode}


    and ro.sender like #{sender}


     and rr.created >= #{startDate}     


     and rr.created <= #{endDate}     

and rr.repertory_type = 2

select,
,su.real_name operatorName ,DATE_FORMAT(rr.created,'%Y-%m-%d') createdStr,
tc.c_name customerName,con.a_code contractCode,av.attr_value storageName
from tb_part_repertory_record rr
left join tb_part_repertory_record_out ro on rr.id = ro.repertory_record_id and ro.deleted = 0
left join tb_customer tc on tc.c_id = ro.customer_id
left join tb_contract con on con.id = ro.contract_id
left join sys_user su on su.user_id = ro.creator
left join sys_attr_value av on rr.storage_id =av.attr_id
where rr.deleted = 0
and rr.repertory_type = 2
and rr.status = 1

    and (tc.c_code like #{customerKey} or tc.c_name like #{customerKey})


    and con.a_code = #{contractCode}


    and rr.code = #{outCode}


    and ro.sender like #{sender}


     and rr.created >= #{startDate}     


     and rr.created <= #{endDate}     

order by rr.id desc
limit #{start} , #{limit}
