mapper.select:
select

from tb_into_stock_bill a
where id = #{id,jdbcType=INTEGER}

select count(1) from tb_into_stock_bill a
where a.deleted = 0

    and code like #{inCode}


    and p_code like #{partCode}


    and p_name like #{partName}


    and DATE_FORMAT(created,'%Y-%m-%d')= #{inDate}

select
,DATE_FORMAT(a.created,'%Y-%m-%d') formatDateIn,a.status,
u.real_name creatorName
from tb_into_stock_bill a
left join sys_user u on a.creator = u.user_id and u.deleted = 0
where a.deleted = 0

    and a.code like #{inCode}


    and a.p_code like #{partCode}


    and a.p_name like #{partName}


    and DATE_FORMAT(a.created,'%Y-%m-%d')= #{inDate}
